/**
 * @dev Calculates the spot price of token Y in token X.
 */
function _calcSpotPrice(
    uint256 amplificationParameter,
    uint256 invariant,
    uint256 balanceX,
    uint256 balanceY
) internal pure returns (uint256) {
    uint256 a = (amplificationParameter * 2) / _AMP_PRECISION;
    uint256 b = invariant + a * (invariant - invariant);
    uint256 numerator = 2 * a * balanceX * balanceY + a * balanceY * balanceY + b * balanceX * balanceX;
    uint256 denominator = 2 * a * balanceX * balanceY + a * balanceX * balanceX + b * balanceY * balanceY;
    return numerator / denominator;
}
