    function store(TriggerOrderStorage storage self, TriggerOrder memory newValue) internal {
        if (newValue.side > uint8.type.max) revert TriggerOrderStorageInvalidError();
        if (newValue.comparison > int8.type.max) revert TriggerOrderStorageInvalidError();
        if (newValue.comparison < int8.type.min) revert TriggerOrderStorageInvalidError();
        if (newValue.fee > UFixed6.wrap(uint64.type.max)) revert TriggerOrderStorageInvalidError();
        if (newValue.price > Fixed6.wrap(int64.type.max)) revert TriggerOrderStorageInvalidError();
        if (newValue.price < Fixed6.wrap(int64.type.min)) revert TriggerOrderStorageInvalidError();
        if (newValue.delta > Fixed6.wrap(int64.type.max)) revert TriggerOrderStorageInvalidError();
        if (newValue.delta < Fixed6.wrap(int64.type.min)) revert TriggerOrderStorageInvalidError();
        if (newValue.interfaceFeeAmount > UFixed6.wrap(uint48.type.max)) revert TriggerOrderStorageInvalidError();

        self.value = StoredTriggerOrder(
            uint8(newValue.side),
            int8(newValue.comparison),
            uint64(UFixed6.unwrap(newValue.fee)),
            int64(Fixed6.unwrap(newValue.price)),
            int64(Fixed6.unwrap(newValue.delta)),
            uint40(UFixed6.unwrap(newValue.interfaceFeeAmount)),
            newValue.interfaceFee.receiver,
            newValue.interfaceFee.unwrap,
