        secondaryPrizePool += rewardToSecondaryPrizePool;

        _swap({
            currentAgentIndex: index,
            lastAgentIndex: currentRoundAgentsAlive,
            agentId: agentId,
            newStatus: AgentStatus.Escaped
        });

        unchecked {
            --currentRoundAgentsAlive;
