    address account,
    address vault,
    address liquidator
) private returns (
    VaultConfig memory vaultConfig,
    VaultAccount memory vaultAccount,
    VaultState memory vaultState
) {
    requireValidAccount(liquidator);
    require(liquidator!= vault);

    require(account!= msg.sender);
    require(account!= liquidator);

