    require(vaultConfig.getFlag(VaultConfiguration.DISABLE_DELEVERAGE) == false);

    if (vaultConfig.getFlag(VaultConfiguration.ONLY_VAULT_DELEVERAGE)) {
        require(msg.sender == vault);
    } else {
