function computeFairReserves(
    uint256 resA,
    uint256 resB,
    uint256 wA,
    uint256 wB,
    uint256 pxA,
    uint256 pxB
) internal pure returns (uint256 fairResA, uint256 fairResB) {
    uint256 r0 = resA / resB;
    uint256 r1 = (wA * pxB) / (wB * pxA);
    if (r0 > r1) {
        uint256 ratio = r1 / r0;
        fairResA = resA * (ratio ** wB);
        fairResB = resB / (ratio ** wA);
    } else {
        uint256 ratio = r0 / r1;
        fairResA = resA / (ratio ** wB);
        fairResB = resB * (ratio ** wA);
    }
}
