    uint256 t0Amount;

    if (JUSDBorrowed <= amount) {
        tBorrowAmount = JUSDBorrowed;
        t0Amount = user.t0BorrowBalance;
    } else {
        tBorrowAmount = amount;
        t0Amount = amount.decimalDiv(tRate);
