function _exactInSingle(Trade memory trade)
    internal
    view
    returns (address target, bytes memory executionCallData)
{
    address sellToken = _getTokenAddress(trade.sellToken);
    address buyToken = _getTokenAddress(trade.buyToken);
    ICurvePool pool = ICurvePool(
        Deployments.CURVE_REGISTRY.find_pool_for_coins(sellToken, buyToken)
    );

    if (address(pool) == address(0)) {
        revert InvalidTrade();
    }

    int128 i = -1;
    int128 j = -1;
    for (int128 c = 0; c < MAX_TOKENS; c++) {
        address coin = pool.coins(uint256(int256(c)));
        if (coin == sellToken) {
            i = c;
        }
        if (coin == buyToken) {
            j = c;
        }
        if (i > -1 && j > -1) {
            break;
        }
    }

    if (i == -1 || j == -1) {
        revert InvalidTrade();
    }

    return (
        address(pool),
        abi.encodeWithSelector(
            ICurvePool.exchange.selector,
            i,
            j,
            trade.amount,
            trade.limit
        )
    );
}
