function lockCapital(address _lendingPoolAddress)
    external
    payable
    override
    onlyDefaultStateManager
    whenNotPaused
    returns (uint256 _lockedAmount, uint256 _snapshotId)
{
    _snapshotId = _snapshot();

    uint256 _length = lendingPoolDetails[_lendingPoolAddress].activeProtectionIndexes.length();
    uint256 _lockedAmount = 0;

    for (uint256 i = 0; i < _length; i++) {
        uint256 _protectionIndex = lendingPoolDetails[_lendingPoolAddress].activeProtectionIndexes.at(i);
        ProtectionInfo storage protectionInfo = protectionInfos[_protectionIndex];

        uint256 _remainingPrincipal = poolInfo.referenceLendingPools.calculateRemainingPrincipal(
            _lendingPoolAddress,
            protectionInfo.buyer,
            protectionInfo.purchaseParams.nftLpTokenId
        );

        uint256 _protectionAmount = protectionInfo.purchaseParams.protectionAmount;
        uint256 _lockedAmountPerProtection = _protectionAmount < _remainingPrincipal
           ? _protectionAmount
            : _remainingPrincipal;

        _lockedAmount += _lockedAmountPerProtection;
    }

    if (totalSTokenUnderlying < _lockedAmount) {
        _lockedAmount = totalSTokenUnderlying;
        totalSTokenUnderlying = 0;
    } else {
        totalSTokenUnderlying -= _lockedAmount;
    }

    return (_lockedAmount, _snapshotId);
}

