        uToken.safeTransfer(config.treasury(), fee);
        withdrawAmount -= fee;
    }

    uToken.safeTransfer(msg.sender, withdrawAmount);

    emit Withdrawn(msg.sender, withdrawAmount, shareAmount);
}
