function combine(
    address adapter,
    uint256 maturity,
    uint256 uBal
) external nonReentrant whenNotPaused returns (uint256 tBal) {
    if (!adapterMeta[adapter].enabled) {
        revert Errors.InvalidAdapter();
    }
    if (!_exists(adapter, maturity)) {
        revert Errors.SeriesDoesNotExist();
    }

    uint256 level = adapterMeta[adapter].level;
    if (level.combineRestricted() && msg.sender!= adapter) {
        revert Errors.CombineRestricted();
    }

    // Burn the PT
    Token(series[adapter][maturity].pt).burn(msg.sender, uBal);

    uint256 collected = _collect(msg.sender, adapter, maturity, uBal, uBal, address(0));

    tBal = uBal / cscale;
    ERC20(Adapter(adapter).target()).safeTransferFrom(adapter, msg.sender, tBal);

    if (!settled) {
        Adapter(adapter).notify(msg.sender, tBal, false);
    }
    unchecked {
        tBal += collected;
    }
    emit Combined(adapter, maturity, tBal, msg.sender);
}
