function _collect(
    address usr,
    address adapter,
    uint256 maturity,
    uint256 uBal,
    uint256 uBalTransfer,
    address to
) internal returns (uint256 collected) {
    if (!_exists(adapter, maturity)) {
        revert Errors.SeriesDoesNotExist();
    }

    if (!adapterMeta[adapter].enabled &&!_settled(adapter, maturity)) {
        revert Errors.InvalidAdapter();
    }

    Series memory _series = series[adapter][maturity];
    uint256 lscale = lscales[adapter][maturity][usr];

    uint256 tBalNow = uBal / _series.maxscale; // preventive round-up towards the protocol
    uint256 tBalPrev = uBal / lscale;
    unchecked {
        collected = tBalPrev > tBalNow? tBalPrev - tBalNow : 0;
    }

    ERC20(Adapter(adapter).target()).safeTransferFrom(adapter, usr, collected);
    Adapter(adapter).notify(usr, collected, false); // Distribute reward tokens
}
