function withdraw(address bridge, uint88 zethAmount)
    external
    nonReentrant
    onlyValidBridge(bridge)
{
    if (zethAmount == 0) revert Errors.ParameterIsZero();
    uint88 fee;
    uint256 withdrawalFee = bridge.withdrawalFee();
    uint256 vault;
    if (bridge == rethBridge || bridge == stethBridge) {
        vault = Vault.CARBON;
