function push(uint128 id) internal {
    AppStorage storage s = LibAppStorage.diamondStorage();
    if (s.fFirst == 0) {
        s.season.fertilizing = true;
        s.fLast = id;
        s.fFirst = id;
    } else if (id <= s.fFirst) {
        setNext(id, s.fFirst);
        s.fFirst = id;
    } else if (id >= s.fLast) {
        setNext(s.fLast, id);
        s.fLast = id;
    } else {
        uint128 prev = s.fFirst;
        uint128 next = getNext(prev);
        while (id > next) {
            prev = next;
            next = getNext(next);
        }
        setNext(prev, id);
        setNext(id, next);
    }
}
