           .delegatecall(dataItems[index]);

        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }

        emit SocketRouteExecuted(routeIds[index], eventDataItems[index]);

        unchecked {
            ++index;
        }
    }
}
```

