function setApprovalForAll(address _operator, bool _approved) external {
    address owner = msg.sender;
    if (owner == _operator) revert ApprovalToCaller();

    operatorApprovals[owner][_operator] = _approved;

    // Clear operator's token approvals if their operator status is revoked
    if (!_approved) {
        for (uint256 lockId in tokenApprovals) {
            if (tokenApprovals[lockId] == _operator) {
