````
function _transferERC20(address token, address to, uint256 amount) internal {
    IERC20 erc20 = IERC20(token);
    require(erc20!= IERC20(address(0)), "Token Address is not an ERC20");
    uint256 initialBalance = erc20.balanceOf(to);
    uint256 actualAmount = erc20.transfer(to, amount);
    require(actualAmount > 0, "ERC20 Transfer failed");
    uint256 balance = erc20.balanceOf(to);
    require(balance >= (initialBalance + actualAmount), "ERC20 Balance check failed");
    return actualAmount;
}
```
