    emit DepositMade(msg.sender, depositIndex, msg.value, tokenAddress);
    //...
    if (tokenAddress == address(0)) {
        // Handle ETH deposit
        //...
    }
} else {
    //...
    deposits.push(Deposit(payable(msg.sender), amount, tokenAddress));
